#sv_list的每一行是一个变异
sv_list=[]
#with open('Results/nonclustered.csv','r') as f:
with open('Results_before/rs3.noncluster.csv','r') as f:#注意这个是所有RS3 noncluster的，然后往我生成的热点里一个一个match
    reader = csv.reader(f)
    for i in reader:
        sv_list.append(i)
title=sv_list[0]
del sv_list[0]



hotspot_list_len=len(hotspot_list[0])
#hotspot_match是一个字典，键值是热点名，值是二维列表，二维列表里每个变异的坐标是一个列表
hotspot_match={}
for item in hotspot_list:
   
    hotspot_chr="chr"+item[0]
    hotspot_start=int(item[1])
    hotspot_end=int(item[2])
    hotspot_name=item[hotspot_list_len-1]#最后一列是热点名
    hotspot_match[hotspot_name]=[]#在字典里加入热点名键值
    for sv_item in sv_list:
        if sv_item[1] != "BND":
            sv_chr=sv_item[2]
            sv_start=int(sv_item[3])
            sv_end=int(sv_item[5])
            if sv_chr==hotspot_chr and ((hotspot_start<sv_start and hotspot_end>sv_start) or (hotspot_start<sv_end and hotspot_end>sv_end)):
                hotspot_match[hotspot_name].append([sv_start,sv_end])

        else:
            sv_chr=sv_item[2]   #如果是BND那么就把同一个start加进去两次
            sv_start=int(sv_item[3])
            sv_end=int(sv_item[3])
            if sv_chr==hotspot_chr and (hotspot_start<sv_start and hotspot_end>sv_start):
                hotspot_match[hotspot_name].append([sv_start,sv_end])


#键值是热点名，value是一个列表，包含了开始和结束位置
