for keyname in hotspot_match:
    #print(keyname)
    if len(hotspot_match[keyname])==0 :
        continue
    
    #找到画图的起始和终止区域
    hotspot_match[keyname]=sorted(hotspot_match[keyname],key=(lambda x:x[0]))
    regionstart=(hotspot_match[keyname])[0][0]
    hotspot_match[keyname]=sorted(hotspot_match[keyname],key=(lambda x:x[1]),reverse=True)
    regionend=(hotspot_match[keyname])[0][1]
    
    #regionend和regionstart设为热点坐标试试
    regionstart=hotspot[keyname][0]
    regionend=hotspot[keyname][1]
    x=[]
    y=[]
    max_y=0
    for i in range(0,10000): #每一个横轴坐标
        xvalue=regionstart+(regionend-regionstart)/10000*i
        x.append(xvalue)
        yvalue=0
        for subregion in hotspot_match[keyname]: #热点里的每一个变异
            if(subregion[0]==subregion[1]):#如果是一个BND
                if( int((subregion[0]-regionstart)/10000) == i):
                    yvalue+=1
                    continue
            if(xvalue>subregion[0] and xvalue<subregion[1]):
                yvalue+=1
        y.append(yvalue) #y是每个地方的叠加变异数
        if yvalue > max_y:
            max_y=yvalue
